mapper.select:
SELECT MERCHANT_ID merchantId, MERCHANT_LOGIN_NAME merchantLoginName, MERCHANT_NAME merchantName, MERCHANT_TEL_PHONE merchantTelPhone, MERCHANT_MOBILEPHONE merchantMobilephone, MERCHANT_BUSINESS_LICENSE merchantBusinessLicense, MERCHANT_ADDRESS merchantAddress, MERCHANT_LONGITUDE merchantLongitude, MERCHANT_LATITUDE merchantLatitude, MERCHANT_ENABLE merchantEnable, CREATE_TIME createTime, LAST_UPDATE_TIME lastUpdateTime FROM MERCHANT WHERE MERCHANT_ENABLE = #{merchantEnable}
AND MERCHANT_NAME LIKE CONCAT('%',#{merchantName},'%')
AND MERCHANT_ADDRESS LIKE CONCAT('%',#{merchantAddress},'%')

mapper.update:
UPDATE
    MERCHANT
SET
    LAST_UPDATE_TIME = now()

    ,MERCHANT_NAME = #{merchantName}


    ,MERCHANT_TEL_PHONE = #{merchantTelPhone}


    ,MERCHANT_MOBILEPHONE = #{merchantMobilephone}


    ,MERCHANT_BUSINESS_LICENSE = #{merchantBusinessLicense}


    ,MERCHANT_ADDRESS = #{merchantAddress}


    ,MERCHANT_LONGITUDE = #{merchantLongitude}


    ,MERCHANT_LATITUDE = #{merchantLatitude}


    ,MERCHANT_ENABLE = #{merchantEnable}

WHERE
    MERCHANT_ID = #{merchantId}
